"use strict";

var page_name="crypto_md5";
var errplace=require('moonlight_function_errplace');


var crypto = require('crypto');
try{
    var crypto_md5=function(input,callback){
        var md5 = crypto.createHash("md5");
        md5.update(input);
        var str = md5.digest('hex');
        var output= str.toUpperCase();  //32位大写
        callback(output);
    };
}catch(err){
    errplace(err,page_name);
}
module.exports=crypto_md5;